import React from 'react';

interface UseCarouselProps {
  rootRef: React.RefObject<HTMLDivElement>;
  defaultVisibleIndex?: number;
}

const useCarousel = ({
  rootRef,
  defaultVisibleIndex = 0,
}: UseCarouselProps): {
  visibleIndex: number[];
  scrollToIndex: (index: number) => void;
} => {
  const [carouselItems, setCarouselItems] = React.useState<Element[]>([]);
  const [visibleIndex, setVisible] = React.useState<number[]>([
    defaultVisibleIndex,
  ]);

  React.useEffect(() => {
    if (rootRef.current) {
      const C = Array.from(rootRef.current.children);
      setCarouselItems(C);

      const options = {
        root: rootRef.current,
        rootMargin: '0px',
        threshold: 1.0,
      };

      const observer = new IntersectionObserver((e) => {
        e.forEach((entry, index) => {
          console.log(index, entry.intersectionRatio);
          if (entry.intersectionRatio === 1) {
            setVisible([C.indexOf(entry.target)]);
          }
        });
      }, options);

      C.forEach((target) => {
        observer.observe(target);
      });
    }
  }, [rootRef.current]);

  return {
    visibleIndex,
    scrollToIndex: (index: number) => {
      carouselItems[index].scrollIntoView({
        behavior: 'smooth',
        block: 'nearest',
        inline: 'start',
      });
    },
  };
};

export { useCarousel };
