import React from "react";

interface CarouselProps {
  children: React.ReactNode;
  className?: string;
}

const Carousel = {
  Root: ({ children, className }: CarouselProps) => {
    const divRef = React.useRef<HTMLDivElement>(null);
    // const [visibleIndex, setVisibleIndex] = React.useState(0);

    React.useEffect(() => {
      if (divRef.current) {
        const C = Array.from(divRef.current.children);

        const options = {
          root: divRef.current,
          rootMargin: "0px",
          threshold: 1.0,
        };

        const observer = new IntersectionObserver((e) => {
          console.log(e);
        }, options);
        C.forEach((target) => {
          observer.observe(target);
        });

        // C[4].scrollIntoView({
        //   behavior: "smooth",
        //   block: "nearest",
        //   inline: "start",
        // });
      }
    }, []);

    return (
      <div
        ref={divRef}
        className={`flex overflow-auto snap-x snap-mandatory ${className}`}
      >
        {children}
      </div>
    );
  },
};

export { Carousel };
