  { children, className }: CarouselProps,
  ref?: RefObject<HTMLDivElement>, // Add the correct type for the ref
) {
  return (
    <div
      className={`flex overflow-auto snap-x snap-mandatory ${className}`}
      ref={ref} // Use the ref passed to the component
    >
      {children}
    </div>
  );
});

