import React, { useEffect } from 'react';
import { Root } from '../carousel';
import { CarouselItem } from '../carouselItem';
import { useCarousel } from '../useCarousel';

interface Carousel1Props {
  items: React.ReactNode[];
}
const Carousel1 = ({ items }: Carousel1Props) => {
  const rootRef = React.useRef<HTMLDivElement>(null);
  const { visibleIndex, scrollToIndex } = useCarousel({ rootRef });
  const prevIndex = React.useRef<number>(0);
  const nextIndex = React.useRef<number>(0);

  useEffect(() => {
    prevIndex.current = Math.max(visibleIndex[0] - 1, 0);
    nextIndex.current = Math.min(
      visibleIndex[visibleIndex.length - 1] + 1,
      items.length - 1,
    );
    console.log(visibleIndex, prevIndex.current, nextIndex.current);
  }, [visibleIndex]);

  return (
    <>
      <Root ref={rootRef} className="border border-black">
        {items.map((_, index) => (
          <CarouselItem
            key={index}
            className="border border-red py-10 w-1/2 shrink-0"
          >
            {index}
          </CarouselItem>
        ))}
      </Root>
      <button onClick={() => scrollToIndex(nextIndex.current)}>Next</button>
      <button onClick={() => scrollToIndex(prevIndex.current)}>Prev</button>
    </>
  );
};

export { Carousel1 };
