import React from 'react';

interface UseCarouselProps {
  rootRef: React.RefObject<HTMLDivElement>;
  defaultVisibleIndex?: number;
}

const useCarousel = ({
  rootRef,
  defaultVisibleIndex = 0,
}: UseCarouselProps): {
  visibleIndexes: number[];
  scrollToIndex: (index: number) => void;
} => {
  const [carouselItems, setCarouselItems] = React.useState<Element[]>([]);
  const [visibleIndex, setVisible] = React.useState(
    new Set<number>([defaultVisibleIndex]),
  );

  React.useEffect(() => {
    if (rootRef.current) {
      const C = Array.from(rootRef.current.children);
      setCarouselItems(C);

      const options = {
        root: rootRef.current,
        rootMargin: '10px',
        threshold: 1.0,
      };

      const observer = new IntersectionObserver((e) => {
        e.forEach((entry) => {
          if (entry.intersectionRatio === 1) {
            visibleIndex.add(C.indexOf(entry.target));
          } else {
            visibleIndex.delete(C.indexOf(entry.target));
          }
        });
        const newSet = new Set(visibleIndex.values());
        if (newSet.size > 0) setVisible(newSet);
      }, options);

      C.forEach((target) => {
        observer.observe(target);
      });
    }
  }, [rootRef.current]);

  const sortedArray = Array.from(visibleIndex.values()).sort();
  return {
    visibleIndex: sortedArray,
    scrollToIndex: (index: number) => {
      const pos = 'nearest';
      carouselItems[index].scrollIntoView({
        behavior: 'smooth',
        block: pos,
        inline: pos,
      });
    },
  };
};

export { useCarousel };
